`,(0,i.jsxDEV)(n.div,{className:"remark-code-container",children:(0,i.jsxDEV)(n.figure,{"data-rehype-pretty-code-figure":"",children:(0,i.jsxDEV)(n.pre,{style:{backgroundColor:"#24292e",color:"#e1e4e8"},tabIndex:"0","data-language":"js","data-theme":"github-dark",raw:`attribute vec2 a_position;

uniform vec2 u_resolution;
uniform vec2 u_translation;
uniform vec2 u_rotation;
uniform vec2 u_scale;

void main() {
  // \u7F29\u653E
  vec2 scaledPosition = a_position * u_scale;

  // \u65CB\u8F6C\u4F4D\u7F6E (xY+yX, yY-xX)
  vec2 rotatedPosition = vec2(scaledPosition.x * u_rotation.y + scaledPosition.y * u_rotation.x, scaledPosition.y * u_rotation.y - scaledPosition.x * u_rotation.x);

  // \u5E73\u79FB
  vec2 position = rotatedPosition + u_translation;

  // \u4ECE\u50CF\u7D20\u5750\u6807\u8F6C\u6362\u5230 0.0 \u5230 1.0
  // vec2 zeroToOne = a_position / u_resolution;
  vec2 zeroToOne = position / u_resolution;

    // \u518D\u628A 0->1 \u8F6C\u6362 0->2
  vec2 zeroToTwo = zeroToOne * 2.0;

    // \u628A 0->2 \u8F6C\u6362\u5230 -1->+1 (\u88C1\u526A\u7A7A\u95F4)
  vec2 clipSpace = zeroToTwo - 1.0;
  gl_Position = vec4(clipSpace * vec2(1, -1), 0, 1);
}
