let line;
while(line = read_line()) {
    let nums = line.split(' ');

    let a = +nums[0];    // \u5C06\u5B57\u7B26\u4E32\u8F6C\u4E3A\u6570\u5B57
    let b = +nums[1];

    console.log(a + b);
}
