      * {
        margin: 0;
        padding: 0; /*清处浏览器默认设置*/
      }
      table {
        /*表格的外边距和大小*/
        margin: 10px 0 0 0;
        width: 100%;
        border-spacing: 0;
        border-collapse: collapse; /*collapse 表格单元格边框合并    border-spacing 表格单元格间距为零  */
      }
