`,(0,n.jsxDEV)(e.div,{className:"remark-code-container",children:(0,n.jsxDEV)(e.figure,{"data-rehype-pretty-code-figure":"",children:(0,n.jsxDEV)(e.pre,{style:{backgroundColor:"#24292e",color:"#e1e4e8"},tabIndex:"0","data-language":"html","data-theme":"github-dark",raw:`<div class="shanxing shanxing1">
  <div class="sx1"></div>
  <div class="sx2"></div>
</div>
<!--*\u7ED8\u5236\u4E00\u4E2A85\u5EA6\u6247\u5F62*/-->
<div class="shanxing shanxing2">
  <div class="sx1"></div>
  <div class="sx2"></div>
</div>
<!--*\u7ED8\u5236\u4E00\u4E2A\u5411\u53F3\u6247\u5F62\uFF0C90\u5EA6\u6247\u5F62*-->
<div class="shanxing shanxing3">
  <div class="sx1"></div>
  <div class="sx2"></div>
</div>
<!--*\u7ED8\u5236\u4E00\u4E2A\u989C\u8272\u6247\u5F62 */-->
<div class="shanxing shanxing4">
  <div class="sx1"></div>
  <div class="sx2"></div>
</div>
<!--/*\u7ED8\u5236\u4E00\u4E2A\u4E0D\u540C\u989C\u8272\u534A\u5706\u5939\u89D2 */-->
<div class="shanxing shanxing5">
  <div class="sx1"></div>
  <div class="sx2"></div>
</div>
<style>
  .shanxing {
    position: relative;
    width: 200px;
    height: 200px;
    border-radius: 100px;
    background-color: yellow;
  }

  .sx1 {
    position: absolute;
    width: 200px;
    height: 200px;
    transform: rotate(0deg);
    clip: rect(
      0px,
      100px,
      200px,
      0px
    ); /*\u8FD9\u4E2Aclip\u5C5E\u6027\u7528\u6765\u7ED8\u5236\u534A\u5706\uFF0C\u5728clip\u7684rect\u8303\u56F4\u5185\u7684\u5185\u5BB9\u663E\u793A\u51FA\u6765\uFF0C\u4F7F\u7528clip\u5C5E\u6027\uFF0C\u5143\u7D20\u5FC5\u987B\u662Fabsolute\u7684 */
    border-radius: 100px;
    background-color: #f00; /*-webkit-animation: an1 2s infinite linear; */
  }

  .sx2 {
    position: absolute;
    width: 200px;
    height: 200px;
    transform: rotate(0deg);
    clip: rect(0px, 100px, 200px, 0px);
    border-radius: 100px;
    background-color: #f00; /*-webkit-animation: an2 2s infinite linear;*/
  }

  /*\u7ED8\u5236\u4E00\u4E2A60\u5EA6\u6247\u5F62*/
  .shanxing1 .sx1 {
    transform: rotate(-30deg);
  }
  .shanxing1 .sx2 {
    transform: rotate(-150deg);
  }

  /*\u7ED8\u5236\u4E00\u4E2A85\u5EA6\u6247\u5F62*/
  .shanxing2 .sx1 {
    transform: rotate(-45deg);
  }
  .shanxing2 .sx2 {
    transform: rotate(-140deg);
  }

  /*\u7ED8\u5236\u4E00\u4E2A\u5411\u53F3\u6247\u5F62\uFF0C90\u5EA6\u6247\u5F62*/
  .shanxing3 .sx1 {
    transform: rotate(45deg);
  }
  .shanxing3 .sx2 {
    transform: rotate(-45deg);
  }

  /*\u7ED8\u5236\u4E00\u4E2A\u989C\u8272\u6247\u5F62 */
  .shanxing4 .sx1 {
    transform: rotate(45deg);
    background-color: #fff;
  }
  .shanxing4 .sx2 {
    transform: rotate(-45deg);
    background-color: #fff;
  }

  /*\u7ED8\u5236\u4E00\u4E2A\u4E0D\u540C\u989C\u8272\u534A\u5706\u5939\u89D2 */
  .shanxing5 .sx1 {
    transform: rotate(45deg);
    background-color: #f00;
  }
  .shanxing5 .sx2 {
    transform: rotate(-45deg);
    background-color: #0f0;
  }
</style>

