`,(0,n.jsxDEV)(e.div,{className:"remark-code-container",children:(0,n.jsxDEV)(e.figure,{"data-rehype-pretty-code-figure":"",children:(0,n.jsxDEV)(e.pre,{style:{backgroundColor:"#24292e",color:"#e1e4e8"},tabIndex:"0","data-language":"html","data-theme":"github-dark",raw:`<head>
  <script>
      const WIDTH = 750;
      const mobileAdapter = () => {
        let scale = screen.width / WIDTH;
        let content = \`width=\${WIDTH}, initial-scale=\${scale}, maximum-scale=\${scale}, minimum-scale=\${scale}\`;
        let meta = document.querySelector("meta[name=viewport]");
        if (!meta) {
          meta = document.createElement("meta");
          meta.setAttribute("name", "viewport");
          document.head.appendChild(meta);
        }
        meta.setAttribute("content", content);
      };

      mobileAdapter();

      window.onorientationchange = mobileAdapter; //\u5C4F\u5E55\u7FFB\u8F6C\u65F6\u518D\u6B21\u6267\u884C
    <\/script>
</head>
