}

.sx2{    position: absolute;    width: 200px;    height: 200px;    transform: rotate(0deg);    clip: rect(0px,100px,200px,0px);    border-radius: 100px;    background-color: #f00;    /*-webkit-animation: an2 2s infinite linear;*/
