`,(0,n.jsxDEV)(e.div,{className:"remark-code-container",children:(0,n.jsxDEV)(e.figure,{"data-rehype-pretty-code-figure":"",children:(0,n.jsxDEV)(e.pre,{style:{backgroundColor:"#24292e",color:"#e1e4e8"},tabIndex:"0","data-language":"js","data-theme":"github-dark",raw:`function debounce(fn, delay) {
  let task = null;
  return function () {
    if (task) {
      clearTimeout();
      task = null;
    }
    task = setTimeout(() => {
      fn.apply(this, arguments);
    }, delay);
  };
}
