`,(0,n.jsxDEV)(e.div,{className:"remark-code-container",children:(0,n.jsxDEV)(e.figure,{"data-rehype-pretty-code-figure":"",children:(0,n.jsxDEV)(e.pre,{style:{backgroundColor:"#24292e",color:"#e1e4e8"},tabIndex:"0","data-language":"js","data-theme":"github-dark",raw:`class Scheduler {
  constructor(limit) {
    this.limit = limit;
    this.queue = [];
    this.count = 0;
  }
  add(time, str) {
    const request = () => {
      return new Promise((resolve) => {
        setTimeout(() => {
          console.log(str);
          resolve();
        }, time);
      });
    };
    this.queue.push(request);
  }
  taskStart() {
    for (let i = 0; i < this.limit; i++) {
      this.request();
    }
  }

  request() {
    if (!this.queue.length || this.count > this.limit) {
      return;
    }
    this.count++;
    this.queue
      .shift()()
      .then(() => {
        this.count--;
        this.request();
      });
  }
}
