`,(0,n.jsx)(l.div,{className:"remark-code-container",children:(0,n.jsx)(l.figure,{"data-rehype-pretty-code-figure":"",children:(0,n.jsx)(l.pre,{style:{backgroundColor:"#24292e",color:"#e1e4e8"},tabIndex:"0","data-language":"js","data-theme":"github-dark",raw:`class _lazyMan {
  constructor(name) {
    const fn = () => {
      console.log(\`Hi! This is \${name}\`);
      this.next();
    };
    this.tasks = [];
    this.tasks.push(fn);
    setTimeout(() => {
      this.next();
    });
  }
  next() {
    const task = this.tasks.shift();
    task && task();
  }
  sleep(delay) {
    this.sleepWrapper(delay);
    return this;
  }
  sleepFirst(delay) {
    this.sleepWrapper(delay, true);
    return this;
  }
  sleepWrapper(time, first) {
    const fn = () => {
      setTimeout(() => {
        console.log(\`Wake up after \${time}\`);
        this.next();
      }, time * 1000);
    };
    if (first) {
      this.tasks.unshift(fn);
    } else {
      this.tasks.push(fn);
    }
  }
  eat(food) {
    const fn = () => {
      console.log(\`Eat \${food}\`);
      this.next();
    };
    this.tasks.push(fn);
    return this;
  }
}

const LazyMan = (name) => {
