`,(0,n.jsx)(l.div,{className:"remark-code-container",children:(0,n.jsx)(l.figure,{"data-rehype-pretty-code-figure":"",children:(0,n.jsx)(l.pre,{style:{backgroundColor:"#24292e",color:"#e1e4e8"},tabIndex:"0","data-language":"js","data-theme":"github-dark",raw:`let str = "0123456789";
let temp = "";
// \u5148\u505A\u4E00\u4E2A 10KB \u7684\u5B57\u7B26\u4E32
while (str.length !== 10240) {
  str = str + "0123456789";
}

// \u5148\u6E05\u7A7A
localStorage.clear();

const computedTotal = () => {
  return new Promise((resolve) => {
    // \u4E0D\u65AD\u5F80 LocalStorage \u4E2D\u7D2F\u79EF\u5B58\u50A8 10KB
    const timer = setInterval(() => {
      try {
        localStorage.setItem("temp", temp);
      } catch {
        // \u62A5\u9519\u8BF4\u660E\u8D85\u51FA\u6700\u5927\u5B58\u50A8
        resolve(temp.length / 1024 - 10);
        clearInterval(timer);
        // \u7EDF\u8BA1\u5B8C\u8BB0\u5F97\u6E05\u7A7A
        localStorage.clear();
      }
      temp += str;
    }, 0);
  });
};

(async () => {
  const total = await computedTotal();
  console.log(\`\u6700\u5927\u5BB9\u91CF\${total}KB\`);
})();
