`,(0,e.jsx)(n.div,{className:"remark-code-container",children:(0,e.jsx)(n.figure,{"data-rehype-pretty-code-figure":"",children:(0,e.jsx)(n.pre,{style:{backgroundColor:"#24292e",color:"#e1e4e8"},tabIndex:"0","data-language":"vue","data-theme":"github-dark",raw:`<template>
  <div class="yzl-box">
    <button ref="yzl_Button" class="yzl-Button" :class="props.type">
      <slot></slot>
    </button>
  </div>
</template>

<script>
import { toRefs, reactive, ref, onMounted } from "vue";
export default {
  name: "yzl-Button",
  props: {
    // \u6309\u94AE\u7C7B\u578B
    type: {
      type: String,
      default: "primary",
      validator: (value) => {
        if (
          value &&
          !["primary", "success", "info", "warning", "danger"].includes(value)
        ) {
          throw new Error("\u7C7B\u578B\u9519\u8BEF");
        }
        return true;
      },
    },
    // \u662F\u5426\u6734\u7D20
    plain: {
      type: Boolean,
      default: false,
    },
  },
  setup(props) {
    const state = reactive({});
    let yzl_Button = ref(null);
    onMounted(() => {
      // \u6734\u7D20\u5143\u7D20
      if (props.plain) {
        yzl_Button.value.classList.add(props.type + "plain");
      }
    });
    return {
      ...toRefs(state),
      props,
      yzl_Button,
    };
  },
};
<\/script>

<style lang="scss" scoped>
@import "./index.scss";
.yzl-box {
  .yzl-Button {
    width: 90px;
    height: 40px;
    border-radius: 5px;
    -webkit-outline: none;
    color: white;
  }

  @each $type,
    $color
      in (
        primary: $primaryBgColor,
        success: $successBgColor,
        info: $infoBgColor,
        warning: $warningBgColor,
        danger: $dangerBgColor
      )
  {
    .#{$type} {
      background-color: #{$color};
      border: none;
      outline: none;
    }
  }

  // \u9F20\u6807\u60AC\u6D6E
  @each $type,
    $color
      in (
        primary: $primaryBgColor,
        success: $successBgColor,
        info: $infoBgColor,
        warning: $warningBgColor,
        danger: $dangerBgColor
      )
  {
    .#{$type}:hover {
      background-color: #{$color};
      opacity: 0.8;
      border: none;
      outline: none;
    }
  }

  // \u6734\u7D20\u6A21\u5F0F
  @each $type,
    $color
      in (
        primaryplain: $primarycPlainBgColor,
        successplain: $successcPlainBgColor,
        infoplain: $infocPlainBgColor,
        warningplain: $warningcPlainBgColor,
        dangerplain: $dangercPlainBgColor
      )
  {
    .#{$type} {
      background-color: #{$color};
      border: none;
      outline: none;
      color: #000;
    }
  }

  @each $type,
    $color
      in (
        primaryplain: $primaryBgColor,
        successplain: $successBgColor,
        infoplain: $infoBgColor,
        warningplain: $warningBgColor,
        dangerplain: $dangerBgColor
      )
  {
    .#{$type} {
      border: none;
      outline: none;
      color: #{$color};
      border: 1px solid #{$color};
    }
  }

  @each $type,
    $color
      in (
        primaryplain: $primaryBgColor,
        successplain: $successBgColor,
        infoplain: $infoBgColor,
        warningplain: $warningBgColor,
        dangerplain: $dangerBgColor
      )
  {
    .#{$type}:hover {
      border: none;
      outline: none;
      color: white;
      border: 1px solid #{$color};
      background-color: #{$color};
    }
  }
}
</style>
