`,(0,e.jsx)(n.div,{className:"remark-code-container",children:(0,e.jsx)(n.figure,{"data-rehype-pretty-code-figure":"",children:(0,e.jsx)(n.pre,{style:{backgroundColor:"#24292e",color:"#e1e4e8"},tabIndex:"0","data-language":"js","data-theme":"github-dark",raw:`import * as echarts from "../ec-canvas/echarts";

Component({
  properties: {
    height: {
      type: String,
    },
    option: {
      type: Object,
    },
  },
  observers: {
    option: function (val) {
      setTimeout(() => {
        this.initChart(val || {});
      }, 0);
    },
  },
  data: {
    chartId: "",
    canvasId: "",
    ec: {
      lazyload: true,
    },
  },
  lifetimes: {
    attached() {
      this.setData({
        chartId: \`chart_\${this.__wxExparserNodeId__}\`,
        canvasId: \`canvas_\${this.__wxExparserNodeId__}\`,
      });
      this.chartComp = this.selectComponent(\`#\${this.data.chartId}\`);
    },
  },
  methods: {
    initChart(option) {
      this.chartComp &&
        this.chartComp.init((canvas, width, height, dpr) => {
          const chart = echarts.init(canvas, null, {
            width: width,
            height: height,
            devicePixelRatio: dpr,
          });
          if (option.tooltip) {
            Object.assign(option.tooltip, {
              confine: true,
              shadowBlur: 0,
              shadowColor: "#e7e8e8",
              shadowOffsetY: 0,
              shadowOffsetX: 0,
              borderColor: "#e7e8e8",
              padding: [
                5, // \u4E0A
                20, // \u53F3
                5, // \u4E0B
                20, // \u5DE6
              ],
            });
          }

          chart.setOption(option);
          this.chart = chart;
          return chart;
        });
    },
  },
});

