`,(0,e.jsx)(n.div,{className:"remark-code-container",children:(0,e.jsx)(n.figure,{"data-rehype-pretty-code-figure":"",children:(0,e.jsx)(n.pre,{style:{backgroundColor:"#24292e",color:"#e1e4e8"},tabIndex:"0","data-language":"js","data-theme":"github-dark",raw:`import { visit } from "unist-util-visit";

// \u83B7\u53D6\u4EE3\u7801\u5757\u5185\u5BB9
export const preProcess = () => (tree) => {
  visit(tree, (node) => {
    if (node?.type === "element" && node?.tagName === "pre") {
      const [codeEl] = node.children;

      if (codeEl.tagName !== "code") return;
      node.properties.raw = codeEl.children?.[0].value;
      // console.log("\u{1F6A8} preProcess ~", node);
    }
  });
};

// \u4EE3\u7801\u5757\u5185\u5BB9\u653E\u8FDBpre.raw
export const postProcess = () => (tree) => {
  visit(tree, "element", (node) => {
    if (node?.type === "element" && node?.tagName === "figure") {
      if (!("data-rehype-pretty-code-figure" in node.properties)) {
        return;
      }

      for (const child of node.children) {
        if (child.tagName === "pre") {
          child.properties["raw"] = node.properties.raw;
          node.properties.raw = undefined;
          // console.log("\u{1F6A8} postProcess ~", child, node.properties.raw);
        }
      }
    }
  });
};


