`,(0,i.jsxDEV)(n.div,{className:"remark-code-container",children:(0,i.jsxDEV)(n.figure,{"data-rehype-pretty-code-figure":"",children:(0,i.jsxDEV)(n.pre,{style:{backgroundColor:"#24292e",color:"#e1e4e8"},tabIndex:"0","data-language":"js","data-theme":"github-dark",raw:`function printSineAndCosineForAnAngle(angleInDegrees) {
  let rotation = [0, 1];
  var angleInRadians = ((360 - angleInDegrees) * Math.PI) / 180;
  rotation[0] = Math.sin(angleInRadians);
  rotation[1] = Math.cos(angleInRadians);
  return rotation;
}
