const rl = readline.createInterface({
  input: process.stdin,
  output: process.stdout,
});

function add(a, b) {
  return a + b;
}

rl.on("line", function (line) {
  const lines = line.split(" ");
  let a = parseInt(lines[0]);
  let b = parseInt(lines[1]);
  const res = add(a, b);
  console.log(res);
});
