`,(0,n.jsxDEV)(e.div,{className:"remark-code-container",children:(0,n.jsxDEV)(e.figure,{"data-rehype-pretty-code-figure":"",children:(0,n.jsxDEV)(e.pre,{style:{backgroundColor:"#24292e",color:"#e1e4e8"},tabIndex:"0","data-language":"js","data-theme":"github-dark",raw:`const readline = require("readline");

const rl = readline.createInterface({
  input: process.stdin,
  output: process.stdout,
});

function add(a, b) {
  return a + b;
}

// \u8BB0\u5F55\u884C\u6570
let lineCount = -1;
// \u8BB0\u5F55\u6BCF\u884C
let lines = [];

rl.on("line", function (line) {
  if (lineCount === -1) {
    lineCount = parseInt(line.trim());
  } else {
    lines.push(line);
    if (lines.length === lineCount) {
      rl.close();
    }
  }
});

rl.on("close", function (line) {
  for (let i = 0; i < lineCount; i++) {
    const line = lines[i];
    const data = line.split(" ");
    let a = parseInt(data[0]);
    let b = parseInt(data[1]);
    const res = add(a, b);
    console.log(res);
  }
});

