console.log();

// \u56DE\u8F93\u5165\u7684\u4E00\u884C\uFF0C\u5B57\u7B26\u4E32\u5F62\u5F0F\u3002\u9700\u8981\u901A\u8FC7\`split\`\u3001\`parseInt\`\u7B49\u65B9\u6CD5\u6765\u5F97\u5230\u771F\u6B63\u7684\u8F93\u5165\u3002(\u8FD9\u91CC\u786E\u5B9E\u6BD4C++\u90A3\u4E9B\u8981\u9EBB\u70E6\u5F97\u591A)
