`,(0,n.jsxDEV)(e.div,{className:"remark-code-container",children:(0,n.jsxDEV)(e.figure,{"data-rehype-pretty-code-figure":"",children:(0,n.jsxDEV)(e.pre,{style:{backgroundColor:"#24292e",color:"#e1e4e8"},tabIndex:"0","data-language":"js","data-theme":"github-dark",raw:`function bfs(grid) {
	let queue = [];
	// \u5C06\u6240\u6709\u7684\u6E90\u5934\u52A0\u5165\u961F\u5217
	for (let i = 0; i < grid.length; i++) {
	  for (let j = 0; j < grid[0].length; j++) {
		if (grid[i][j] === 1) {
		  queue.push([i, j]);
		}
	  }
	}

	const dx = [0, 0, 1, -1];
	const dy = [1, -1, 0, 0];

	while (queue.length > 0) {
	  let [r, c] = queue.shift();

	  // \u4E0A\u4E0B\u5DE6\u53F3\u56DB\u4E2A\u65B9\u5411, \u6807\u8BB0 \u5DF2\u904D\u5386
	  for (let k = 0; k < 4; k++) {
		let x = r + dx[k];
		let y = c + dy[k];
		if (inArea(grid, x, y) && grid[x][y] == 0) {
		  grid[x][y] = -1;
		  queue.push([x, y]);
		}
	  }
	}
}

// \u5224\u65AD\u5750\u6807 (r, c) \u662F\u5426\u5728\u7F51\u683C\u4E2D
function inArea(grid, r, c) {
	return 0 <= r && r < grid.length && 0 <= c && c < grid[0].length;
