`,(0,n.jsx)(l.div,{className:"remark-code-container",children:(0,n.jsx)(l.figure,{"data-rehype-pretty-code-figure":"",children:(0,n.jsx)(l.pre,{style:{backgroundColor:"#24292e",color:"#e1e4e8"},tabIndex:"0","data-language":"js","data-theme":"github-dark",raw:`function bfs(grid, i, j) {
  let queue = [];
  queue.push([i, j]);

  while (queue.length > 0) {
    let len = queue.length;

    // \u904D\u5386\u5355\u5C42\u6240\u6709\u8282\u70B9
    for (let i = 0; i < len; i++) {
      let [r, c] = queue.shift();

      // \u4E0A\u4E0B\u5DE6\u53F3\u56DB\u4E2A\u65B9\u5411, \u6807\u8BB0 \u5DF2\u904D\u5386
      if (inArea(grid, r - 1, c) && grid[r - 1][c] === 0) {
        grid[r - 1][c] = -1;
        queue.push([r - 1, c]);
      }
      if (inArea(grid, r + 1, c) && grid[r + 1][c] === 0) {
        grid[r + 1][c] = -1;
        queue.push([r + 1, c]);
      }
      if (inArea(grid, r, c - 1) && grid[r][c - 1] === 0) {
        grid[r][c - 1] = -1;
        queue.push([r, c - 1]);
      }
      if (inArea(grid, r, c + 1) && grid[r][c + 1] === 0) {
        grid[r][c + 1] = -1;
        queue.push([r, c + 1]);
      }
    }
  }
}

// \u5224\u65AD\u5750\u6807 (r, c) \u662F\u5426\u5728\u7F51\u683C\u4E2D
function inArea(grid, r, c) {
	return 0 <= r && r < grid.length && 0 <= c && c < grid[0].length;
}
