`,(0,n.jsxDEV)(e.div,{className:"remark-code-container",children:(0,n.jsxDEV)(e.figure,{"data-rehype-pretty-code-figure":"",children:(0,n.jsxDEV)(e.pre,{style:{backgroundColor:"#24292e",color:"#e1e4e8"},tabIndex:"0","data-language":"js","data-theme":"github-dark",raw:`function dfs(grid, r, c) {
    // \u5224\u65AD base case
    if (!inArea(grid, r, c)) {
        return;
    }

	if (grid[r][c] !== '1') return;
	// \u6807\u8BB0 \u5DF2\u7ECF\u904D\u5386 \u201C\u201D
	grid[r][c] = '2';

    // \u8BBF\u95EE\u4E0A\u3001\u4E0B\u3001\u5DE6\u3001\u53F3\u56DB\u4E2A\u76F8\u90BB\u7ED3\u70B9
    dfs(grid, r - 1, c);
    dfs(grid, r + 1, c);
    dfs(grid, r, c - 1);
    dfs(grid, r, c + 1);
}

// \u5224\u65AD\u5750\u6807 (r, c) \u662F\u5426\u5728\u7F51\u683C\u4E2D
function inArea(grid, r, c) {
    return 0 <= r && r < grid.length 
		&& 0 <= c && c < grid[0].length;
}
