`,(0,n.jsxDEV)(e.p,{children:["\u7F51\u683C\u56FE\u7684\u5C42\u5E8F\u904D\u5386, \u53EF\u4EE5\u89E3\u51B3\u6700\u77ED\u8DEF\u5F84\u95EE\u9898\u3002",(0,n.jsxDEV)(e.mark,{children:"Dijkstra \u7B97\u6CD5\u89E3\u51B3\u7684\u662F\u5E26\u6743\u6700\u77ED\u8DEF\u5F84\u95EE\u9898\uFF0C\u5BF9\u4E8E\u65E0\u6743\u6700\u77ED\u8DEF\u5F84\u95EE\u9898\uFF08\u8FB9\u6743\u4E3A0/1\uFF09\u7528 BFS \u6C42\u89E3\u5C31\u884C\u4E86"},void 0,!1,{fileName:"/Users/heavenmei/Desktop/heavenmei.io/content/post/_mdx_bundler_entry_point-363139e7-f518-4b11-895b-32cce45babd2.mdx",lineNumber:92,columnNumber:22},this),"\u3002 ",(0,n.jsxDEV)(e.a,{href:"https://leetcode.cn/problems/as-far-from-land-as-possible/",children:"1162. \u5730\u56FE\u5206\u6790"},void 0,!1,{fileName:"/Users/heavenmei/Desktop/heavenmei.io/content/post/_mdx_bundler_entry_point-363139e7-f518-4b11-895b-32cce45babd2.mdx",lineNumber:92,columnNumber:81},this),"\xA0"]},void 0,!0,{fileName:"/Users/heavenmei/Desktop/heavenmei.io/content/post/_mdx_bundler_entry_point-363139e7-f518-4b11-895b-32cce45babd2.mdx",lineNumber:92,columnNumber:1},this),`
`,(0,n.jsxDEV)(e.div,{className:"remark-code-container",children:(0,n.jsxDEV)(e.figure,{"data-rehype-pretty-code-figure":"",children:(0,n.jsxDEV)(e.pre,{style:{backgroundColor:"#24292e",color:"#e1e4e8"},tabIndex:"0","data-language":"js","data-theme":"github-dark",raw:`function bfs(grid, i, j) {
  let queue = [];
  queue.push([i, j]);

  while (queue.length > 0) {
    let len = queue.length;

    // \u904D\u5386\u5355\u5C42\u6240\u6709\u8282\u70B9
    for (let i = 0; i < len; i++) {
      let [r, c] = queue.shift();

      // \u4E0A\u4E0B\u5DE6\u53F3\u56DB\u4E2A\u65B9\u5411, \u6807\u8BB0 \u5DF2\u904D\u5386
      if (inArea(grid, r - 1, c) && grid[r - 1][c] === 0) {
        grid[r - 1][c] = -1;
        queue.push([r - 1, c]);
      }
      if (inArea(grid, r + 1, c) && grid[r + 1][c] === 0) {
        grid[r + 1][c] = -1;
        queue.push([r + 1, c]);
      }
      if (inArea(grid, r, c - 1) && grid[r][c - 1] === 0) {
        grid[r][c - 1] = -1;
        queue.push([r, c - 1]);
      }
      if (inArea(grid, r, c + 1) && grid[r][c + 1] === 0) {
        grid[r][c + 1] = -1;
        queue.push([r, c + 1]);
      }
    }
  }
}

// \u5224\u65AD\u5750\u6807 (r, c) \u662F\u5426\u5728\u7F51\u683C\u4E2D
function inArea(grid, r, c) {
	return 0 <= r && r < grid.length && 0 <= c && c < grid[0].length;
