`,(0,n.jsxDEV)(e.p,{children:"In a situation where each browser window has its own shared state object that does not share anything other than the object structure with the shared state object of the other windows, we will need to modify our state to be a map of window IDs to states:"},void 0,!1,{fileName:"/Users/heavenmei/Desktop/heavenmei.io/content/post/_mdx_bundler_entry_point-5e632650-c79b-4f37-8c8b-2f1605890d52.mdx",lineNumber:102,columnNumber:1},this),`
`,(0,n.jsxDEV)(e.div,{className:"remark-code-container",children:(0,n.jsxDEV)(e.figure,{raw:`import { State, updateState } from './state';

export const sharedState: Map<number, State> = new Map();

chrome.windows.getAll({ populate: true }, (windows) => {
  windows.forEach((window) => {
    \`chrome.storage\`.local.get(\`sharedState-\${window.id}\`, (result) => {
      const windowState = result[\`sharedState-\${window.id}\`];
      if (windowState) {
        sharedState.set(window.id, windowState);
      } else {
        // Initialize state for this window
        sharedState.set(window.id, {
          // Initialize your per-window shared state here
        });
      }
    });
  });
});

chrome.runtime.onMessage.addListener((message, sender, sendResponse) => {
  if (message.type === 'UPDATE_STATE') {
    const { windowId, newState } = message.payload;
    const windowState = sharedState.get(windowId);
    if (windowState) {
      updateState(windowState, newState);
      // Save the updated state
      \`chrome.storage\`.local.set({ [\`sharedState-\${windowId}\`]: windowState });
      // Notify all tabs of the state change
      chrome.tabs.query({ windowId }, (tabs) => {
        tabs.forEach((tab) => {
          chrome.tabs.sendMessage(tab.id!, {
            type: 'STATE_UPDATED',
            payload: windowState,
          });
        });
      });
    }
  }
});
