`,(0,n.jsxDEV)(e.p,{children:["The shared state object in the background script is not persisted across browser sessions. To handle this, you can use ",(0,n.jsxDEV)(e.code,{children:"chrome.storage.local"},void 0,!1,{fileName:"/Users/heavenmei/Desktop/heavenmei.io/content/post/_mdx_bundler_entry_point-7e57c35c-20b7-416a-81aa-4dc3814c2c11.mdx",lineNumber:64,columnNumber:120},this)," to store the shared state and load it when the extension starts up:"]},void 0,!0,{fileName:"/Users/heavenmei/Desktop/heavenmei.io/content/post/_mdx_bundler_entry_point-7e57c35c-20b7-416a-81aa-4dc3814c2c11.mdx",lineNumber:64,columnNumber:1},this),`
`,(0,n.jsxDEV)(e.div,{className:"remark-code-container",children:(0,n.jsxDEV)(e.figure,{raw:`import { State, updateState } from './state';

export const sharedState: State = {
  // Initialize your shared state here
};

\`chrome.storage\`.local.get('sharedState', (result) => {
  if (result.sharedState) {
    Object.assign(sharedState, result.sharedState);
  }
});

chrome.runtime.onMessage.addListener((message, sender, sendResponse) => {
  if (message.type === 'UPDATE_STATE') {
    updateState(sharedState, message.payload);
    // Save the updated state
    \`chrome.storage\`.local.set({ sharedState });
    // Notify all tabs of the state change
    chrome.tabs.query({}, (tabs) => {
      tabs.forEach((tab) => {
        chrome.tabs.sendMessage(tab.id!, {
          type: 'STATE_UPDATED',
          payload: sharedState,
        });
      });
    });
  }
});
